import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';
import './App.css';

function TodoApp() {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3000c/todos')
      .then(response => setTodos(response.data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const addTodo = (todo) => {
    axios.post('http://localhost:3000c/todos', todo)
      .then(response => setTodos([...todos, response.data]))
      .catch(error => console.error('Error adding todo:', error));
  };

  const toggleTodo = (id) => {
    const todo = todos.find(todo => todo.id === id);
    axios.put(`http://localhost:3000c/todos/${id}`, { 
      completed: !todo.completed, 
      due_date: todo.due_date, 
      due_time: todo.due_time 
    })
      .then(() => setTodos(todos.map(todo => 
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )))
      .catch(error => console.error('Error updating todo:', error));
  };

  const deleteTodo = (id) => {
    axios.delete(`http://localhost:3000c/todos/${id}`)
      .then(() => setTodos(todos.filter(todo => todo.id !== id)))
      .catch(error => console.error('Error deleting todo:', error));
  };

  return (
    <div className="app">
      <h1>오늘 할 일</h1>
      <TodoForm addTodo={addTodo} />
      <TodoList todos={todos} toggleTodo={toggleTodo} deleteTodo={deleteTodo} />
      <p>
        {todos.length === 0
          ? "아직 아무것도 안했어.."
          : todos.filter(todo => !todo.completed).length === 0
          ? "다했다!!"
          : `${todos.filter(todo => !todo.completed).length} 개나 남았어...`}
      </p>
    </div>
  );
}

export default TodoApp;
