import React, { useState } from 'react';

function generateCalendar(year, month) {
  const firstDay = new Date(year, month, 1).getDay();
  const lastDate = new Date(year, month + 1, 0).getDate();

  const daysInCalendar = [];
  let currentDay = 1 - firstDay;

  for (let i = 0; i < 6; i++) {
    const week = [];
    for (let j = 0; j < 7; j++) {
      if (currentDay > 0 && currentDay <= lastDate) {
        week.push(currentDay);
      } else {
        week.push(null);
      }
      currentDay++;
    }
    daysInCalendar.push(week);
  }

  return daysInCalendar;
}

function CustomCalendar({ onDateSelect }) {
  const [currentDate, setCurrentDate] = useState(new Date());

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const daysInCalendar = generateCalendar(year, month);

  const handlePrevMonth = () => {
    setCurrentDate(new Date(year, month - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(year, month + 1, 1));
  };

  const handleDateClick = (day) => {
    if (day !== null) {
      onDateSelect(new Date(year, month, day));
    }
  };

  return (
    <div>
      <div>
        <button onClick={handlePrevMonth}>이전</button>
        <span>{year}년 {month + 1}월</span>
        <button onClick={handleNextMonth}>다음</button>
      </div>
      <div>
        <div className='day'>일 월 화 수 목 금 토</div>
        {daysInCalendar.map((week, i) => (
          <div key={i} style={{ display: 'flex' }}>
            {week.map((day, j) => (
              <div 
                key={j} 
                style={{ flex: 1, textAlign: 'center', padding: '5px', cursor: 'pointer' }} 
                onClick={() => handleDateClick(day)}
              >
                {day ? day : ''}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default CustomCalendar;
