import React, { useState } from 'react';
import { startOfMonth, endOfMonth, startOfWeek, addDays, format, isSameDay } from 'date-fns';

function CustomCalendar({ todos, onDateSelect }) {
  const [currentDate, setCurrentDate] = useState(new Date());

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();
  const today = new Date();

  const daysInCalendar = generateCalendar(year, month);

  const handlePrevMonth = () => {
    setCurrentDate(new Date(year, month - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(year, month + 1, 1));
  };

  const handleDateClick = (date) => {
    onDateSelect(date);
  };

  const isAllTodosCompleted = (date) => {
    const formattedDate = format(date, 'yyyy-MM-dd');
    const todosForDate = todos.filter(todo => todo.created_at === formattedDate);
    return todosForDate.length > 0 && todosForDate.every(todo => todo.completed);
  };

  return (
    <div>
      <div>
        <button onClick={handlePrevMonth}>이전</button>
        <span>{format(currentDate, 'yyyy년 M월')}</span>
        <button onClick={handleNextMonth}>다음</button>
      </div>
      <div className='day'>
        <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '30px' }}>
          {['일', '월', '화', '수', '목', '금', '토'].map((dayName, index) => (
            <div
              key={index}
              style={{
                flex: 0.1,
                textAlign: 'center',
                padding: '10px',
                color: dayName === '일' ? 'red' : dayName === '토' ? 'blue' : 'black'
              }}
            >
              {dayName}
            </div>
          ))}
        </div>
        {daysInCalendar.map((week, i) => (
          <div key={i} style={{ display: 'flex', justifyContent: 'center' }}>
            {week.map((day, j) => {
              const isCurrentMonth = format(day, 'M') === format(currentDate, 'M');
              const isToday = isSameDay(day, today);
              const allTodosCompleted = isAllTodosCompleted(day);

              return (
                <div
                  key={j}
                  style={{
                    flex: 0.1,
                    textAlign: 'center',
                    padding: '10px',
                    cursor: 'pointer',
                    backgroundColor: allTodosCompleted ? 'green' : isToday ? 'lightblue' : '',
                    color: isCurrentMonth ? 'black' : '#ddd'
                  }}
                  onClick={() => handleDateClick(day)}
                >
                  {format(day, 'd')}
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
}

function generateCalendar(year, month) {
  const firstDayOfMonth = startOfMonth(new Date(year, month));
  const lastDayOfMonth = endOfMonth(firstDayOfMonth);
  const firstDayOfWeek = startOfWeek(firstDayOfMonth);

  const daysInCalendar = [];
  let currentDay = firstDayOfWeek;

  while (currentDay <= lastDayOfMonth || daysInCalendar.length < 6) {
    const week = [];
    for (let i = 0; i < 7; i++) {
      week.push(currentDay);
      currentDay = addDays(currentDay, 1);
    }
    daysInCalendar.push(week);
  }

  return daysInCalendar;
}

export default CustomCalendar;
