import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Calendar from 'react-calendar';
import Modal from 'react-modal';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';
import './App.css';
import 'react-calendar/dist/Calendar.css';

Modal.setAppElement('#root');

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());

  useEffect(() => {
    fetchTodosForDate(selectedDate);
  }, [selectedDate]);

  const fetchTodosForDate = (date) => {
    const formattedDate = date.toISOString().split('T')[0];
    axios.get(`http://localhost:3000/todos/${formattedDate}`)
      .then(response => setTodos(response.data))
      .catch(error => console.error('Error fetching data:', error));
  };

  const addTodo = (text) => {
    axios.post('http://localhost:3000/todos', { text })
      .then(response => fetchTodosForDate(selectedDate))
      .catch(error => console.error('Error adding todo:', error));
  };

  const toggleTodo = (id) => {
    const todo = todos.find(todo => todo.id === id);
    axios.put(`http://localhost:3000/todos/${id}`, { completed: !todo.completed })
      .then(() => fetchTodosForDate(selectedDate))
      .catch(error => console.error('Error updating todo:', error));
  };

  const deleteTodo = (id) => {
    axios.delete(`http://localhost:3000/todos/${id}`)
      .then(() => fetchTodosForDate(selectedDate))
      .catch(error => console.error('Error deleting todo:', error));
  };

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const handleDateChange = (date) => {
    setSelectedDate(date);
    closeModal();
  };

  return (
    <div className="app">
      <h1>
        Ïò§Îäò Ìï† Ïùº
        <button onClick={openModal} className="calendar-button">
          üìÖ
        </button>
      </h1>
      <TodoForm addTodo={addTodo} />
      <TodoList todos={todos} toggleTodo={toggleTodo} deleteTodo={deleteTodo} />
      <p>
        {todos.length === 0
          ? "ÏïÑÏßÅ ÏïÑÎ¨¥Í≤ÉÎèÑ ÏïàÌñàÏñ¥.."
          : todos.filter(todo => !todo.completed).length === 0
          ? "Îã§ÌñàÎã§!!"
          : `${todos.filter(todo => !todo.completed).length} Í∞úÎÇò ÎÇ®ÏïòÏñ¥...`}
      </p>
      <Modal 
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Calendar Modal"
        className="calendar-modal"
        overlayClassName="calendar-overlay"
      >
        <Calendar onChange={handleDateChange} value={selectedDate} />
        <button onClick={closeModal}>Îã´Í∏∞</button>
      </Modal>
    </div>
  );
}

export default TodoApp;
